        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/DeFAIManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title DeFAIManager
 * @dev Main contract for managing DeFi AI operations in the FinNexus platform
 * @notice Handles portfolio management, yield optimization, and automated trading
 */
contract DeFAIManager is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant STRATEGY_ROLE = keccak256("STRATEGY_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");
    
    // Token contracts
    IERC20 public immutable nexusToken;
    IERC20 public immutable potToken;
    
    // Portfolio management
    struct Portfolio {
        address owner;
        uint256 totalValue;
        uint256 lastRebalance;
        bool isActive;
    }
    
    mapping(address => Portfolio) public portfolios;
    mapping(address => mapping(address => uint256)) public portfolioTokenBalances;
    mapping(address => address[]) public portfolioSupportedTokens;
    mapping(address => bool) public supportedTokens;
    address[] public tokenList;
    
    // Yield optimization
    struct YieldStrategy {
        address strategyContract;
        string name;
        bool isActive;
        uint256 minDeposit;
        uint256 maxDeposit;
        uint256 currentAPY;
        uint256 totalDeposited;
    }
    
    mapping(uint256 => YieldStrategy) public yieldStrategies;
    uint256 private _strategyIdCounter;
    
    // User yield tracking
    mapping(address => uint256) public userYield;
    mapping(address => uint256[]) public userStrategies;
    
    // Risk management
    struct RiskProfile {
        uint256 riskScore; // 0-1000 (1000 = highest risk)
        uint256 maxLeverage;
        uint256 maxSinglePosition;
        bool isApproved;
    }
    
    mapping(address => RiskProfile) public riskProfiles;
    
    // Fee configuration
    uint256 public managementFeePercentage = 200; // 2% (200 basis points)
    uint256 public performanceFeePercentage = 1000; // 10% (1000 basis points)
    address public feeRecipient;
    
    // Events
    event PortfolioCreated(address indexed owner, uint256 initialValue);
    event PortfolioRebalanced(address indexed owner, uint256 newValue);
    event YieldOptimized(address indexed user, uint256 amount, uint256 strategyId);
    event StrategyAdded(uint256 indexed strategyId, address strategyContract, string name);
    event StrategyUpdated(uint256 indexed strategyId, bool isActive, uint256 newAPY);
    event RiskProfileUpdated(address indexed user, uint256 riskScore, bool isApproved);
    event FeesCollected(address indexed user, uint256 managementFee, uint256 performanceFee);
    event EmergencyWithdrawal(address indexed user, uint256 amount, string reason);
    
    // Modifiers
    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "DeFAIManager: must have admin role");
        _;
    }
    
    modifier onlyOperator() {
        require(hasRole(OPERATOR_ROLE, msg.sender), "DeFAIManager: must have operator role");
        _;
    }
    
    modifier onlyStrategy() {
        require(hasRole(STRATEGY_ROLE, msg.sender), "DeFAIManager: must have strategy role");
        _;
    }
    
    modifier onlyEmergency() {
        require(hasRole(EMERGENCY_ROLE, msg.sender), "DeFAIManager: must have emergency role");
        _;
    }
    
    modifier onlyPortfolioOwner(address user) {
        require(portfolios[user].owner == user, "DeFAIManager: not portfolio owner");
        _;
    }

    constructor(address _nexusToken, address _potToken, address _feeRecipient) {
        require(_nexusToken != address(0), "DeFAIManager: nexus token cannot be zero address");
        require(_potToken != address(0), "DeFAIManager: pot token cannot be zero address");
        require(_feeRecipient != address(0), "DeFAIManager: fee recipient cannot be zero address");
        
        nexusToken = IERC20(_nexusToken);
        potToken = IERC20(_potToken);
        feeRecipient = _feeRecipient;
        
        // Set up roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(STRATEGY_ROLE, msg.sender);
        _grantRole(EMERGENCY_ROLE, msg.sender);
        
        // Initialize strategy counter
        _strategyIdCounter = 1; // Start from 1
        
        // Add initial supported tokens
        supportedTokens[_nexusToken] = true;
        supportedTokens[_potToken] = true;
        tokenList.push(_nexusToken);
        tokenList.push(_potToken);
    }

    /**
     * @dev Create a new portfolio for a user
     * @param user Address of the user
     * @param initialValue Initial portfolio value
     */
    function createPortfolio(address user, uint256 initialValue) external onlyOperator {
        require(user != address(0), "DeFAIManager: user cannot be zero address");
        require(!portfolios[user].isActive, "DeFAIManager: portfolio already exists");
        require(initialValue > 0, "DeFAIManager: initial value must be greater than 0");
        
        portfolios[user] = Portfolio({
            owner: user,
            totalValue: initialValue,
            lastRebalance: block.timestamp,
            isActive: true
        });
        
        // Initialize supported tokens for this portfolio
        for (uint256 i = 0; i < tokenList.length; i++) {
            portfolioSupportedTokens[user].push(tokenList[i]);
        }
        
        emit PortfolioCreated(user, initialValue);
    }

    /**
     * @dev Optimize yield for a user
     * @param user Address of the user
     * @param amount Amount to optimize
     * @param strategyId Strategy to use
     */
    function optimizeYield(address user, uint256 amount, uint256 strategyId) external onlyOperator nonReentrant {
        require(user != address(0), "DeFAIManager: user cannot be zero address");
        require(amount > 0, "DeFAIManager: amount must be greater than 0");
        require(portfolios[user].isActive, "DeFAIManager: portfolio not active");
        require(yieldStrategies[strategyId].isActive, "DeFAIManager: strategy not active");
        require(amount >= yieldStrategies[strategyId].minDeposit, "DeFAIManager: amount below minimum");
        require(amount <= yieldStrategies[strategyId].maxDeposit, "DeFAIManager: amount above maximum");
        
        // Check user's risk profile
        require(riskProfiles[user].isApproved, "DeFAIManager: risk profile not approved");
        
        // Transfer tokens from user to contract
        nexusToken.safeTransferFrom(user, address(this), amount);
        
        // Update user yield
        userYield[user] += amount;
        
        // Add strategy to user's strategies
        userStrategies[user].push(strategyId);
        
        // Update strategy total deposited
        yieldStrategies[strategyId].totalDeposited += amount;
        
        emit YieldOptimized(user, amount, strategyId);
    }

    /**
     * @dev Rebalance user's portfolio
     * @param user Address of the user
     */
    function rebalancePortfolio(address user) external onlyOperator {
        require(user != address(0), "DeFAIManager: user cannot be zero address");
        require(portfolios[user].isActive, "DeFAIManager: portfolio not active");
        
        // Update portfolio value based on current market conditions
        // This would integrate with oracle services in a real implementation
        portfolios[user].lastRebalance = block.timestamp;
        
        emit PortfolioRebalanced(user, portfolios[user].totalValue);
    }

    /**
     * @dev Add a new yield strategy
     * @param strategyContract Address of the strategy contract
     * @param name Name of the strategy
     * @param minDeposit Minimum deposit amount
     * @param maxDeposit Maximum deposit amount
     * @param initialAPY Initial APY percentage
     */
    function addYieldStrategy(
        address strategyContract,
        string memory name,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint256 initialAPY
    ) external onlyAdmin {
        require(strategyContract != address(0), "DeFAIManager: strategy contract cannot be zero address");
        require(minDeposit > 0, "DeFAIManager: min deposit must be greater than 0");
        require(maxDeposit >= minDeposit, "DeFAIManager: max deposit must be >= min deposit");
        
        uint256 strategyId = _strategyIdCounter;
        _strategyIdCounter++;
        
        yieldStrategies[strategyId] = YieldStrategy({
            strategyContract: strategyContract,
            name: name,
            isActive: true,
            minDeposit: minDeposit,
            maxDeposit: maxDeposit,
            currentAPY: initialAPY,
            totalDeposited: 0
        });
        
        emit StrategyAdded(strategyId, strategyContract, name);
    }

    /**
     * @dev Update strategy parameters
     * @param strategyId Strategy ID to update
     * @param isActive Whether the strategy is active
     * @param newAPY New APY percentage
     */
    function updateStrategy(uint256 strategyId, bool isActive, uint256 newAPY) external onlyAdmin {
        require(strategyId > 0 && strategyId < _strategyIdCounter, "DeFAIManager: invalid strategy ID");
        
        yieldStrategies[strategyId].isActive = isActive;
        yieldStrategies[strategyId].currentAPY = newAPY;
        
        emit StrategyUpdated(strategyId, isActive, newAPY);
    }

    /**
     * @dev Update user's risk profile
     * @param user Address of the user
     * @param riskScore Risk score (0-1000)
     * @param maxLeverage Maximum leverage allowed
     * @param maxSinglePosition Maximum single position size
     * @param isApproved Whether the profile is approved
     */
    function updateRiskProfile(
        address user,
        uint256 riskScore,
        uint256 maxLeverage,
        uint256 maxSinglePosition,
        bool isApproved
    ) external onlyAdmin {
        require(user != address(0), "DeFAIManager: user cannot be zero address");
        require(riskScore <= 1000, "DeFAIManager: risk score cannot exceed 1000");
        
        riskProfiles[user] = RiskProfile({
            riskScore: riskScore,
            maxLeverage: maxLeverage,
            maxSinglePosition: maxSinglePosition,
            isApproved: isApproved
        });
        
        emit RiskProfileUpdated(user, riskScore, isApproved);
    }

    /**
     * @dev Collect management and performance fees
     * @param user Address of the user
     * @param portfolioValue Current portfolio value
     * @param performanceGain Performance gain since last fee collection
     */
    function collectFees(address user, uint256 portfolioValue, uint256 performanceGain) external onlyOperator {
        require(user != address(0), "DeFAIManager: user cannot be zero address");
        
        uint256 managementFee = (portfolioValue * managementFeePercentage) / 10000;
        uint256 performanceFee = (performanceGain * performanceFeePercentage) / 10000;
        
        uint256 totalFees = managementFee + performanceFee;
        
        if (totalFees > 0) {
            nexusToken.safeTransfer(feeRecipient, totalFees);
            emit FeesCollected(user, managementFee, performanceFee);
        }
    }

    /**
     * @dev Emergency withdrawal function
     * @param user Address to withdraw for
     * @param amount Amount to withdraw
     * @param reason Reason for emergency withdrawal
     */
    function emergencyWithdrawal(address user, uint256 amount, string memory reason) external onlyEmergency nonReentrant {
        require(user != address(0), "DeFAIManager: user cannot be zero address");
        require(amount > 0, "DeFAIManager: amount must be greater than 0");
        require(nexusToken.balanceOf(address(this)) >= amount, "DeFAIManager: insufficient contract balance");
        
        nexusToken.safeTransfer(user, amount);
        
        emit EmergencyWithdrawal(user, amount, reason);
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyAdmin {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyAdmin {
        _unpause();
    }

    /**
     * @dev Get user's strategies
     * @param user Address of the user
     * @return Array of strategy IDs
     */
    function getUserStrategies(address user) external view returns (uint256[] memory) {
        return userStrategies[user];
    }

    /**
     * @dev Get supported tokens
     * @return Array of supported token addresses
     */
    function getSupportedTokens() external view returns (address[] memory) {
        return tokenList;
    }

    /**
     * @dev Get total strategies count
     * @return Total number of strategies
     */
    function getTotalStrategies() external view returns (uint256) {
        return _strategyIdCounter - 1;
    }
}

contracts/NexusToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Capped.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title NexusToken
 * @dev ERC20 token for the FinNexus AI platform with enhanced security features
 * @notice This contract implements a capped, pausable, burnable ERC20 token with role-based access control
 */
contract NexusToken is ERC20, ERC20Burnable, ERC20Pausable, ERC20Capped, AccessControl, ReentrancyGuard {
    // Role definitions
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    
    // Token configuration
    uint256 public constant INITIAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens
    uint256 public constant MAX_SUPPLY = 2_000_000_000 * 10**18; // 2 billion tokens cap
    
    // Fee configuration
    uint256 public transferFeePercentage = 250; // 2.5% (250 basis points)
    uint256 public burnFeePercentage = 100; // 1% (100 basis points)
    address public feeRecipient;
    
    // Anti-whale protection
    uint256 public maxTransferAmount = 10_000_000 * 10**18; // 10M tokens max per transfer
    bool public antiWhaleEnabled = true;
    
    // Events
    event TransferFeeUpdated(uint256 newFeePercentage);
    event BurnFeeUpdated(uint256 newFeePercentage);
    event FeeRecipientUpdated(address newFeeRecipient);
    event MaxTransferAmountUpdated(uint256 newAmount);
    event AntiWhaleToggled(bool enabled);
    event FeesCollected(address indexed from, uint256 transferFee, uint256 burnFee);
    
    // Modifiers
    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "NexusToken: must have admin role");
        _;
    }
    
    modifier onlyMinter() {
        require(hasRole(MINTER_ROLE, msg.sender), "NexusToken: must have minter role");
        _;
    }
    
    modifier onlyPauser() {
        require(hasRole(PAUSER_ROLE, msg.sender), "NexusToken: must have pauser role");
        _;
    }
    
    modifier antiWhaleCheck(address from, address to, uint256 amount) {
        if (antiWhaleEnabled && !hasRole(ADMIN_ROLE, from) && !hasRole(ADMIN_ROLE, to)) {
            require(amount <= maxTransferAmount, "NexusToken: transfer amount exceeds maximum");
        }
        _;
    }

    constructor(address _feeRecipient) 
        ERC20("Nexus Token", "NEXUS")
        ERC20Capped(MAX_SUPPLY)
    {
        require(_feeRecipient != address(0), "NexusToken: fee recipient cannot be zero address");
        
        // Set up roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        
        // Set fee recipient
        feeRecipient = _feeRecipient;
        
        // Mint initial supply
        _mint(msg.sender, INITIAL_SUPPLY);
        
        emit FeeRecipientUpdated(_feeRecipient);
    }

    /**
     * @dev Mint tokens to a specific address
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyMinter {
        _mint(to, amount);
    }

    /**
     * @dev Pause token transfers
     */
    function pause() external onlyPauser {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyPauser {
        _unpause();
    }

    /**
     * @dev Update transfer fee percentage
     * @param newFeePercentage New fee percentage in basis points (100 = 1%)
     */
    function updateTransferFee(uint256 newFeePercentage) external onlyAdmin {
        require(newFeePercentage <= 1000, "NexusToken: fee cannot exceed 10%");
        transferFeePercentage = newFeePercentage;
        emit TransferFeeUpdated(newFeePercentage);
    }

    /**
     * @dev Update burn fee percentage
     * @param newFeePercentage New burn fee percentage in basis points (100 = 1%)
     */
    function updateBurnFee(uint256 newFeePercentage) external onlyAdmin {
        require(newFeePercentage <= 500, "NexusToken: burn fee cannot exceed 5%");
        burnFeePercentage = newFeePercentage;
        emit BurnFeeUpdated(newFeePercentage);
    }

    /**
     * @dev Update fee recipient address
     * @param newFeeRecipient New fee recipient address
     */
    function updateFeeRecipient(address newFeeRecipient) external onlyAdmin {
        require(newFeeRecipient != address(0), "NexusToken: fee recipient cannot be zero address");
        feeRecipient = newFeeRecipient;
        emit FeeRecipientUpdated(newFeeRecipient);
    }

    /**
     * @dev Update maximum transfer amount for anti-whale protection
     * @param newAmount New maximum transfer amount
     */
    function updateMaxTransferAmount(uint256 newAmount) external onlyAdmin {
        require(newAmount > 0, "NexusToken: max transfer amount must be greater than 0");
        maxTransferAmount = newAmount;
        emit MaxTransferAmountUpdated(newAmount);
    }

    /**
     * @dev Toggle anti-whale protection
     * @param enabled Whether to enable anti-whale protection
     */
    function toggleAntiWhale(bool enabled) external onlyAdmin {
        antiWhaleEnabled = enabled;
        emit AntiWhaleToggled(enabled);
    }

    /**
     * @dev Override transfer function to include fees and anti-whale protection
     */
    function transfer(address to, uint256 amount) 
        public 
        override 
        whenNotPaused 
        nonReentrant 
        antiWhaleCheck(msg.sender, to, amount) 
        returns (bool) 
    {
        uint256 transferFee = calculateTransferFee(amount);
        uint256 burnFee = calculateBurnFee(amount);
        uint256 netAmount = amount - transferFee - burnFee;
        
        // Collect fees
        if (transferFee > 0) {
            _transfer(msg.sender, feeRecipient, transferFee);
        }
        
        if (burnFee > 0) {
            _burn(msg.sender, burnFee);
        }
        
        // Transfer net amount
        _transfer(msg.sender, to, netAmount);
        
        emit FeesCollected(msg.sender, transferFee, burnFee);
        
        return true;
    }

    /**
     * @dev Override transferFrom function to include fees and anti-whale protection
     */
    function transferFrom(address from, address to, uint256 amount) 
        public 
        override 
        whenNotPaused 
        nonReentrant 
        antiWhaleCheck(from, to, amount) 
        returns (bool) 
    {
        uint256 transferFee = calculateTransferFee(amount);
        uint256 burnFee = calculateBurnFee(amount);
        uint256 netAmount = amount - transferFee - burnFee;
        
        // Collect fees
        if (transferFee > 0) {
            _transfer(from, feeRecipient, transferFee);
        }
        
        if (burnFee > 0) {
            _burn(from, burnFee);
        }
        
        // Transfer net amount
        _transfer(from, to, netAmount);
        
        // Update allowance
        uint256 currentAllowance = allowance(from, msg.sender);
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(from, msg.sender, currentAllowance - amount);
        }
        
        emit FeesCollected(from, transferFee, burnFee);
        
        return true;
    }

    /**
     * @dev Calculate transfer fee for a given amount
     * @param amount Amount to calculate fee for
     * @return Fee amount
     */
    function calculateTransferFee(uint256 amount) public view returns (uint256) {
        return (amount * transferFeePercentage) / 10000;
    }

    /**
     * @dev Calculate burn fee for a given amount
     * @param amount Amount to calculate fee for
     * @return Fee amount
     */
    function calculateBurnFee(uint256 amount) public view returns (uint256) {
        return (amount * burnFeePercentage) / 10000;
    }

    /**
     * @dev Get total fees for a transfer amount
     * @param amount Amount to calculate fees for
     * @return transferFee Transfer fee amount
     * @return burnFee Burn fee amount
     * @return totalFees Total fees amount
     */
    function getTransferFees(uint256 amount) external view returns (uint256 transferFee, uint256 burnFee, uint256 totalFees) {
        transferFee = calculateTransferFee(amount);
        burnFee = calculateBurnFee(amount);
        totalFees = transferFee + burnFee;
    }

    // Required overrides for multiple inheritance
    function _update(address from, address to, uint256 value) internal override(ERC20, ERC20Capped, ERC20Pausable) {
        super._update(from, to, value);
    }
}

contracts/PoTToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title PoTToken (Proof of Trust Token)
 * @dev ERC20 token representing trust and reputation in the FinNexus AI platform
 */
contract PoTToken is ERC20, ERC20Burnable, ERC20Pausable, AccessControl, ReentrancyGuard {

    // Role definitions
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    
    // Token configuration
    uint256 public constant MAX_SUPPLY = 100_000_000 * 10**18;
    uint256 public totalMinted;
    uint256 public totalBurned;
    
    // Trust scoring system
    struct TrustScore {
        uint256 score; // 0-1000 (1000 = maximum trust)
        uint256 lastUpdated;
        string reason;
    }
    
    mapping(address => TrustScore) public trustScores;
    mapping(address => bool) public isWhitelisted;
    
    // Reward categories
    enum RewardCategory {
        TRADING_PERFORMANCE,    // 1.0x multiplier
        COMPLIANCE_SCORE,       // 1.2x multiplier
        COMMUNITY_CONTRIBUTION, // 1.5x multiplier
        PLATFORM_LOYALTY,      // 2.0x multiplier
        SPECIAL_ACHIEVEMENT    // 3.0x multiplier
    }
    
    mapping(RewardCategory => uint256) public rewardMultipliers;
    
    // Events
    event TrustScoreUpdated(address indexed user, uint256 newScore, string reason);
    event RewardMinted(address indexed user, uint256 amount, RewardCategory category, string reason, uint256 trustScore);
    event PenaltyApplied(address indexed user, uint256 amount, string reason);
    
    constructor() ERC20("Proof of Trust Token", "POT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);
        
        rewardMultipliers[RewardCategory.TRADING_PERFORMANCE] = 100;
        rewardMultipliers[RewardCategory.COMPLIANCE_SCORE] = 120;
        rewardMultipliers[RewardCategory.COMMUNITY_CONTRIBUTION] = 150;
        rewardMultipliers[RewardCategory.PLATFORM_LOYALTY] = 200;
        rewardMultipliers[RewardCategory.SPECIAL_ACHIEVEMENT] = 300;
    }

    function mintReward(address to, uint256 baseAmount, RewardCategory category, string memory reason) external {
        require(hasRole(MINTER_ROLE, msg.sender), "PoTToken: must have minter role");
        require(to != address(0), "PoTToken: cannot mint to zero address");
        require(baseAmount > 0, "PoTToken: amount must be greater than 0");
        
        uint256 multiplier = rewardMultipliers[category];
        uint256 trustScore = trustScores[to].score;
        uint256 trustBonus = (multiplier * trustScore) / 10000;
        uint256 finalMultiplier = multiplier + trustBonus;
        uint256 finalAmount = (baseAmount * finalMultiplier) / 100;
        
        require(totalMinted + finalAmount <= MAX_SUPPLY, "PoTToken: would exceed max supply");
        
        totalMinted += finalAmount;
        _mint(to, finalAmount);
        
        emit RewardMinted(to, finalAmount, category, reason, trustScore);
    }

    function applyPenalty(address from, uint256 amount, string memory reason) external {
        require(hasRole(BURNER_ROLE, msg.sender), "PoTToken: must have burner role");
        require(from != address(0), "PoTToken: cannot burn from zero address");
        require(amount > 0, "PoTToken: amount must be greater than 0");
        require(balanceOf(from) >= amount, "PoTToken: insufficient balance for penalty");
        
        totalBurned += amount;
        _burn(from, amount);
        
        emit PenaltyApplied(from, amount, reason);
    }

    function updateTrustScore(address user, uint256 newScore, string memory reason) external {
        require(hasRole(ADMIN_ROLE, msg.sender), "PoTToken: must have admin role");
        require(user != address(0), "PoTToken: user cannot be zero address");
        require(newScore <= 1000, "PoTToken: trust score cannot exceed 1000");
        
        trustScores[user] = TrustScore({
            score: newScore,
            lastUpdated: block.timestamp,
            reason: reason
        });
        
        emit TrustScoreUpdated(user, newScore, reason);
    }

    function pause() external {
        require(hasRole(PAUSER_ROLE, msg.sender), "PoTToken: must have pauser role");
        _pause();
    }

    function unpause() external {
        require(hasRole(PAUSER_ROLE, msg.sender), "PoTToken: must have pauser role");
        _unpause();
    }

    function _update(address from, address to, uint256 value) internal override(ERC20, ERC20Pausable) {
        super._update(from, to, value);
    }
}

